import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex);


//公共state
const state={
    user:{
        age: '',
        communityId: '',
        id:'',
        isvalid: "",
        name: "",
        no: "",
        password: "",
        phone: "",
        roleId: '',
        sex:'',
    },
    residentList:[
        {

        }
    ]
}

//唯一取值的方法，计算属性
const getters= {
    getUser(state){
        return state.user;
    }
}

//
const mutations={
    updateUser(state,user){
        state.user=user;
    }
}

//
const actions={
    asyncUpdateUser(context, user) {
        context.commit('updateUser',user);
    }
}

export default new Vuex.Store({
    state,
    getters,
    mutations,
    actions,
})
