import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex);


//公共state
const state={
    user:{
        name:''
    }
}

//唯一取值的方法，计算属性
const getters= {
    getUser(state){
        return state.user;
    }
}

//
const mutations={
    updateUser(state,user){
        state.user=user;
    }
}

//
const actions={
    asyncUpdateUser(context, user) {
        context.commit('UpdateUser',user);
    }
}

export default new Vuex.Store({
    state,
    getters,
    mutations,
    actions,
})
